import React, { component } from "react";
import Axios from "axios";

class Login extends React.Component {
    constructor(props) {
      super(props);
      this.state = {name: '',password:''};
  
      this.handleChangeName = this.handleChangeName.bind(this);
      this.handleChangePassword = this.handleChangePassword.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
  
    handleChangeName(event) {
        this.setState({name: event.target.value});
    }
    
    handleChangePassword(event) {
        this.setState({password:event.target.value});
    }
  
    handleSubmit(event) {
      alert('A name was submitted: ' + this.state.name +' - '+ this.state.password);
      event.preventDefault();
    }
  
    render() {
      return (
        <form onSubmit={this.handleSubmit}>
          <label>
            Name:
            <input type="text" value={this.state.name} onChange={this.handleChangeName} name ="name" required/>
          </label>
          <label>
          Password:
          <input type="password" value={this.state.password} onChange={this.handleChangePassword} password = "password" required/>
          </label>
          <input type="submit" value="Submit" />
        </form>
      );
    }
  }
  
  ReactDOM.render(
    <Login />,
    document.getElementById('root')
  );

export default Login;
